WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/10.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/14.0/apex.js")}
{!REQUIRESCRIPT('/support/console/31.0/integration.js')};

var recordId = window.location.pathname.substring(1);
var returnURL = '/' + recordId;

var urlStart = 'https://'+window.location.hostname+'/apex/LiveText__LiveTextOptinStatusPage?records='+recordId+'&retURL='+returnURL;

if(sforce.console.isInConsole()) {
sforce.console.openPrimaryTab(null, urlStart + '&inConsole=yes&optType=0', true, 'Opt-out for Text Messages');
}else{
window.top.location.href = urlStart + '&inConsole=no&optType=0';
}
